mapper.select:
select id id, parent_id parent_id, menu_title menu_title, menu_level menu_level, sort sort, score score, is_delete is_delete, updated_time updated_time, created_time created_time from fke_supervision_template where 1 = 1
and id=#{id}
and parent_id=#{parent_id}

mapper.update:
update fke_supervision_template set
menu_level=#{menu_level}, menu_title=#{menu_title}, score=#{score},
id=#{id}
